        html .tie-insta-avatar a:before {
            content: "";
            position: absolute;
            width: calc(100% + 6px);
            height: calc(100% + 6px);
            left: -3px;
            top: -3px;
            border-radius: 50%;
            background: #d6249f;
            background: radial-gradient(circle at 30% 107%, #fdf497 0%, #fdf497 5%, #fd5949 45%, #d6249f 60%, #285AEB 90%);
        }

        html .tie-insta-avatar a:after {
            position: absolute;
            content: "";
            width: calc(100% + 3px);
            height: calc(100% + 3px);
            left: -2px;
            top: -2px;
            border-radius: 50%;
            background: #fff;
